import React, { Component } from 'react';
import { Card, Button, Collapse } from 'antd';
import QuestionInput from './QuestionInput'

class TestCreator extends Component {
  constructor(props){
    super(props);
    this.state = {
      visible: false,
      questionInputs: [],
     }

    this.myRef = React.createRef();
  }


   showModal = () => {
     this.setState({
       visible: true,
     });
   }

   handleOk = (e) => {
     console.log(e);
     this.setState({
       visible: false,
     });
   }

   handleCancel = (e) => {
     console.log(e);
     this.setState({
       visible: false,
     });
   }

 addShortAnswer = () => {
   console.log('added!')
   let { questionInputs } = this.state;
   let key = Math.random(1, 300).toString()
   questionInputs.push(<QuestionInput ref={this.myRef} key={ key } uniqueKey={ key } />)
   this.setState({ questionInputs })
 }

  render() {
    let { questionInputs } = this.state;
    // let val = this.refs.shortAnswer;//.value;
    // console.log(val)
    console.log(questionInputs);
    return (
      <div>
        <Button type="primary" style={{ float: 'right' }} onClick={ () => this.addShortAnswer() }>
          +
        </Button>

        <Card
          title="Short Answer Question"
          extra={<div></div>}
          style={{ width: '60%', marginLeft: 'auto', marginRight: 'auto' }}
          >
            { this.state.questionInputs }
        </Card>
      </div>

    )
  };
}

// <Button type="primary" onClick={this.showModal}>
