import React, { Component } from 'react';
import { Switch, Select, Divider } from 'antd';

const Option = Select.Option;
const options = [
  {
    firstName: 'Alex',
    lastName: 'Jones',
    gender: 'male',
    graduationYear: '2020'
  }
]

class NameInput extends Component {
  state = {

  };
  render() {
    let renderedOptions = options.map(o => {
      return <Option value={`${o.firstName}${o.lastName}${o.graduationYear}`}>{o.firstName} {o.lastName} <span style={{ float: 'right' }}>hi</span></Option>
    })
    return (
      <div>
        <Divider orientation="right">Name</Divider>
        <div className={ 'variable-label' } style={{ width: 50 }} >Name:</div>
          <Select
            showSearch
            style={{ width: 200 }}
            placeholder="Select a person"
            optionFilterProp="children"
            filterOption={(input, option) => option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0}
          >
            {renderedOptions}
          </Select>
      </div>
    )
  };
}

export default NameInput;
