import React, { Component } from 'react';
import { Input, InputNumber, Tooltip, Divider, Form } from 'antd';
import VariableForm from './VariableForm'
import VariablePicker from './VariablePicker'

class QuestionInput extends Component {
  constructor(props){
    super(props);
    this.state = {
      value: '',
      variables: [],
    };
  }

  handleChange = event => {
    let { value } = event.target;
    this.setState({ value });
    this.parseVariableNames(value);
  }

  parseVariableNames(value) {
    let variableRegex = /\{.*?\}/g;
    let matches = value.match(variableRegex);
    if (matches){
      matches = [...new Set(matches)]
      let counter = -1;
      matches = matches.map(m => m.replace("{", "").replace("}", ""));

      let variables = []

      matches.map(m => {
        counter++;
        if (matches.length != this.state.variables.length){
          // new variable created

          let match = this.state.variables.filter(k => k == m)
          if (match.length > 0){
            // variable already exists

            variables.push(match[0])
            // console.log(match)
          } else{
            // new variable
            match = {
              key: counter,
              name: m,
            }
            variables.push(match)

          }
          //
        } else {
          // unrelated update or rename
          let match = this.state.variables.filter(k => k.key == counter);
          variables.push(match[0])
          console.log('possible rename took place')
        }
      });
      console.log(variables)
      this.setState({ variables })
    }
  }

  createVariable(item) {
    let { name, min, max, step } = item;

    return (
      <VariableForm
        min={ min }
        max={ max }
        step={ step }
        name={ name } />
    )
  }

  render() {
    let { variables } = this.state;
    // console.log(variables)
    variables = variables.map(v => v.key);//this.createVariable(v));
    return (
      <div>
        <Input
          value={ this.state.value }
          onChange={event => this.handleChange(event) }
          onBlur={event => this.handleChange(event) }
          />
        <Form>
          {variables}
        </Form>
      </div>

    )
  };
}

export default QuestionInput;
