import React, { Component } from 'react';
import { Card, Button, Collapse } from 'antd';
import QuestionInput from './QuestionInput'

class TestCreator extends Component {
  constructor(props){
    super(props);
    this.state = {
      visible: false,
      questionInputs: [

       ],
     }

    this.myRef = React.createRef();
  }


   showModal = () => {
     this.setState({
       visible: true,
     });
   }

   handleOk = (e) => {
     console.log(e);
     this.setState({
       visible: false,
     });
   }

   handleCancel = (e) => {
     console.log(e);
     this.setState({
       visible: false,
     });
   }

 addShortAnswer = () => {
   let { questionInputs } = this.state;
   let key = Math.random(1, 300).toString()
   questionInputs.push(<QuestionInput ref={this.myRef} key={ key } uniqueKey={ key } />)
   this.setState({ questionInputs })
 }

 handleExport = () => {
   const val = this.myRef.current;
   if (val !== null) {
     val.exportAsJSON();
   }
 }

  render() {
    let { questionInputs } = this.state;
    console.log(questionInputs === [], typeof(questionInputs))
    if (questionInputs === []){
      console.log('hi');
      // this.addShortAnswer();
      // questionInputs = this.state.questionInputs;
    }
    // console.log('variables', JSON.stringify(questionInputs));
    return (
      <Card
        title="Example Test #1"
        extra={<Button onClick={() => this.handleExport() } type="dashed">Export</Button>}
        style={{ width: '80%', marginLeft: 'auto', marginRight: 'auto' }}
      >
      <div>
        <Button type="primary" style={{ float: 'right' }} onClick={ () => this.addShortAnswer() }>
          +
        </Button>
        <Card
          title="Short Answer Question"
          extra={<div></div>}
          style={{ width: '60%', marginLeft: 'auto', marginRight: 'auto' }}
          >
            { this.state.questionInputs }
        </Card>
      </div>
    </Card>

    )
  };
}

// <Button type="primary" onClick={this.showModal}>
//   Open Modal
// </Button>
