import React, { Component } from 'react';
import { Input, InputNumber, Tooltip, Divider, Form, Switch, Cascader } from 'antd';

function toTitleCase(str) {
        return str.replace(
            /\w\S*/g,
            function(txt) {
                return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
            }
        );
    }

function convertToDict(arr){
  arr = arr.map(l => {
        return ({
          value: l,
          label: l.toTitle(),
        })
      });

  return arr
}

const englishLetters = convertToDict('ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz'.split(''));
const greekLetters = convertToDict('ΑαΒβΓγΔδΕεΖζΗηΘθΙιΚκΛλΜμΝνΞξΟοΠπΡρΣσΤτΥυΦφΧχΨψΩω'.split(''));

const metricUnits = convertToDict(['meter', 'gram', 'second', 'ampere', 'kelvin', 'mole', 'candela', 'liter'].sort());
const derivedUnits = convertToDict(['radian', 'newton', 'pascal', 'joule',
                                    'watt', 'coulomb', 'volt', 'farad', 'ohm',
                                    'tesla', 'celsius', 'hour', 'minute'].sort());

const customaryLength = convertToDict(['inch', 'foot', 'yard', 'mile'].sort())
const customaryArea = convertToDict(['acre'].sort())
const customaryMass = convertToDict(['ounce', 'pound', 'ton',].sort())
const customaryVolume = convertToDict(['teaspoon', 'tablespoon', 'fluid ounce',
                                       'cup', 'pint', 'quart', 'gallon', ].sort())

const options = [
  // {
  //   value: 'englishLetters',
  //   label: 'English Letters',
  //   children: [
  //     ...englishLetters
  //   ],
  // },
  // {
  //   value: 'greekLetters',
  //   label: 'Greek Letters',
  //   children: [
  //     ...greekLetters
  //   ],
  // },
  {
    value: 'metricUnits',
    label: 'Metric Units',
    children: [
      ...metricUnits
    ],
  },
  {
    value: 'derivedUnits',
    label: 'Derived SI Units',
    children: [
      ...derivedUnits
    ],
  },
  {
    value: 'customaryUnits',
    label: 'Customary Units',
    children: [
      {
        value: 'customaryArea',
        label: 'Area',
        children: [
          ...customaryArea
        ],
      },
      {
        value: 'customaryMass',
        label: 'Mass',
        children: [
          ...customaryMass
        ],
      },
      {
        value: 'customaryLength',
        label: 'Length',
        children: [
          ...customaryLength
        ],
      },
      {
        value: 'customaryVolume',
        label: 'Volume',
        children: [
          ...customaryVolume
        ],
      },
    ],
  },
];

class VariablePicker extends Component {
  state = {};
  render() {
    return (
      <div>
        <div className={ 'variable-label' } >Unit:</div>
        <Cascader
          size={'small'}
          options={options}
          placeholder="Please select"
          style={{ width: '50%', marginTop: '5%', marginLeft: '3%' }} />
        <br />
        <div className={ 'variable-label' } style={{ width: '50px' }}>Metric:</div>
        <Switch size="small" defaultChecked />
      </div>
    )
  };
}

export default VariablePicker;
